import gulp from "gulp";
import browserify from "browserify";
import uglify from "gulp-uglify";
import babelify from "babelify";
import source from "vinyl-source-stream";
import buffer from "vinyl-buffer";
import rename from "gulp-rename";
import cleanCSS from "gulp-clean-css";
import sass from "gulp-sass";
import del from "del";
import fileInclude from "gulp-file-include";
import imagemin from "gulp-imagemin";
import htmlmin from "gulp-htmlmin";
import browserSyncPackage from "browser-sync";

const browserSync = browserSyncPackage.create();

const { reload } = browserSync;

const paths = {
  styles: {
    src: "src/styles/**/*.scss",
    dest: "dist/styles/"
  },
  scripts: {
    dev: "src/scripts/**/*.js",
    src: "src/scripts/index.js",
    dest: "dist/scripts/"
  },
  html: {
    src: "src/routes/*.html",
    template: "src/templates/*.html",
    pages: "src/pages/*.html",
    dest: "dist/"
  },
  images: {
    src: "src/images/*",
    dest: "dist/images"
  },
  fonts: {
    src: "src/fonts/*",
    dest: "dist/fonts"
  }
};

export const clean = () => del(["dist"]);

export function styles() {
  return gulp
    .src(paths.styles.src)
    .pipe(sass().on("error", sass.logError))
    .pipe(cleanCSS())
    .pipe(
      rename({
        basename: "main",
        suffix: ".min"
      })
    )
    .pipe(gulp.dest(paths.styles.dest));
}

export function stylesDev() {
  return gulp
    .src(paths.styles.src)
    .pipe(sass().on("error", sass.logError))
    .pipe(cleanCSS())
    .pipe(
      rename({
        basename: "main",
        suffix: ".min"
      })
    )
    .pipe(gulp.dest(paths.styles.dest))
    .pipe(browserSync.stream());
}

export function scripts() {
  return browserify(paths.scripts.src)
    .transform("babelify", { presets: ["@babel/preset-env"] })
    .bundle()
    .pipe(source("main.min.js"))
    .pipe(buffer())
    .pipe(gulp.dest(paths.scripts.dest));
}

export function minifyScripts() {
  return browserify(paths.scripts.src)
    .transform("babelify", { presets: ["@babel/preset-env"] })
    .bundle()
    .pipe(source("main.min.js"))
    .pipe(buffer())
    .pipe(uglify())
    .pipe(gulp.dest(paths.scripts.dest));
}

export function html() {
  return gulp
    .src(paths.html.src)
    .pipe(
      fileInclude({
        context: {
          env: "prod"
        },
        prefix: "@@",
        basepath: "@file"
      })
    )
    .pipe(gulp.dest(paths.html.dest));
}

export function htmlDev() {
  return gulp
    .src(paths.html.src)
    .pipe(
      fileInclude({
        context: {
          env: "dev"
        },
        prefix: "@@",
        basepath: "@file"
      })
    )
    .pipe(gulp.dest(paths.html.dest));
}

export function minifyHtml() {
  return gulp
    .src(`${paths.html.dest}/*.html`)
    .pipe(htmlmin({ collapseWhitespace: true }))
    .pipe(gulp.dest(paths.html.dest));
}

export function images() {
  return gulp
    .src(paths.images.src)
    .pipe(
      imagemin([
        imagemin.gifsicle({ interlaced: true }),
        imagemin.mozjpeg({ progressive: true }),
        imagemin.optipng({ optimizationLevel: 5 }),
        imagemin.svgo({
          plugins: [{ removeViewBox: true }, { cleanupIDs: false }]
        })
      ])
    )
    .pipe(gulp.dest(paths.images.dest));
}

export function fontsDev() {
  return gulp.src(paths.fonts.src).pipe(gulp.dest(paths.fonts.dest));
}

export function imagesDev() {
  return gulp.src(paths.images.src).pipe(gulp.dest(paths.images.dest));
}

export function gulplisten() {
  gulp.watch(paths.styles.src, stylesDev);
  gulp.watch(paths.scripts.dev, scripts);
  gulp.watch([paths.html.pages, paths.html.src, paths.html.template], htmlDev);
  gulp.watch([paths.images.src], imagesDev);
  gulp.watch([paths.fonts.src], fontsDev);
}

export function server() {
  browserSync.init({
    server: {
      baseDir: "./dist/",
      index: "index.html",
      serveStaticOptions: {
        extensions: ["html"]
      }
    }
  });

  gulp
    .watch([
      paths.scripts.dev,
      paths.html.pages,
      paths.html.src,
      paths.html.template,
      paths.images.src,
      paths.fonts.src
    ])
    .on("change", reload);
}

const dev = gulp.series(
  clean,
  htmlDev,
  styles,
  scripts,
  imagesDev,
  fontsDev,
  gulp.parallel([server, gulplisten])
);

gulp.task("dev", dev);

const build = gulp.series(
  clean,
  html,
  minifyHtml,
  gulp.parallel(styles, minifyScripts, images, fontsDev)
);

gulp.task("build", build);

export default build;
